step2.code:
import { provideNUIConfig } from '@nui/core';
import { myCustomPreset } from './themes/custom';

export const appConfig: ApplicationConfig = {
  providers: [
    provideNUIConfig({
      theme: {
        preset: 'custom',
        customPresets: [myCustomPreset],
        darkMode: {
          strategy: 'manual'
        }
      }
    })
  ]
};

step3.code:
import { ThemeService } from '@nui/core';

export class MyComponent {
  constructor(private themeService: ThemeService) {}

  switchToCustomTheme() {
    this.themeService.setPreset('custom');
  }

  switchToAura() {
    this.themeService.setPreset('aura');
  }
}

